export function getIconMapping(name?: string) {
  return name && name in iconMapping ? iconMapping[name as iconNamesType] : "";
}

export function getSvgMapping(name?: string) {
  return name && name in svgIconMapping ? svgIconMapping[name as svgIconNamesType] : "";
}

export function getFileActionIconMapping(action?: FileActionType) {
  return action && action in fileActionIconMapping ? fileActionIconMapping[action] : "";
}

export type iconNamesType = keyof typeof iconMapping;
export type svgIconNamesType = keyof typeof svgIconMapping;